<?php/* * Shipping estimated date of arrival plugin */if (!class_exists('Shipping_Method_Estimate')) {    class Shipping_Method_Estimate {        public function __construct() {          //  add_filter('woocommerce_cart_shipping_method_full_label', array($this, 'shipping_method_estimate_label', 10, 2));                    }                function shipping_method_estimate_label($label, $method) {            $label .= '<br /><small>';           $meta = $method->get_meta_data();                     if (!strstr($label, "R+L Carriers")) {                            if(isset($meta['days'])){                                        $days = $meta['days'];                                    }                              if(isset($days) && intval($days) === 1){                                    $label .= 'Est delivery: 1 day.';                                  }elseif(isset($days) && intval($days) > 1){                                    $label .= 'Est delivery: ' . $days . ' days.';                                  }else{                                 $label .= '';                            }                              }else{                       $label .= '';        }            $label .= '</small>';            return $label;        }    }    new Shipping_Method_Estimate();}